import React from "react";
import "../css/VolunteerProfile.css";
import avatar from "../images/avatar.jpg";
import { Link } from "react-router-dom";
function copyURLToClipboard() {
  // Create a temporary input element
  const tempInput = document.createElement("input");

  // Set its value to the current URL
  tempInput.value = window.location.href;

  // Append the input element to the document
  document.body.appendChild(tempInput);

  // Select the input's content
  tempInput.select();

  // Execute the copy command
  document.execCommand("copy");

  // Remove the temporary input element
  document.body.removeChild(tempInput);

  // Alert the user that the URL has been copied (optional)
  alert("URL copied to clipboard: " + window.location.href);
}
const VolunteerProfile = () => {
  const user = {
    name: "John Doe",
    avatar: "https://example.com/avatar.jpg",
  };

  return (
    <>
      <div className="profile-page">
        <div className="user-info">
          <img src={avatar} alt="User Avatar" />
          <h2>{user.name}</h2>
          <p className="p-first">
            Your membership in the DrishtiConnect family is appreciated.
          </p>
          <p className="p-second">
            As a valued member, we invite you to access your certificate by
            clicking the download button.
          </p>
        </div>
        <div className="middle-button">
          <Link to="/certificate">
            <button class="btn btn-primary ">Generate</button>
          </Link>
          <button class="btn btn-primary ">Logout</button>
        </div>
      </div>
      <div>
        <button type="button" onClick={copyURLToClipboard}>
          Copy URL
        </button>
      </div>
    </>
  );
};

export default VolunteerProfile;
